import React, { useState } from 'react';
import './login.css'; 
import axios from 'axios';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const loginWithFacebook = () => {
    window.location.href = 'https://www.facebook.com/login';
  };

  const loginWithGoogle = () => {
    window.location.href = 'https://accounts.google.com';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post('http://localhost:3000/api/login', {
        email,
        password,
      });

      if (res.data.success) {
        alert('Đăng nhập thành công!');
        // Lưu token hoặc thông tin người dùng nếu cần
        // localStorage.setItem('token', res.data.token);
      } else {
        alert('Sai thông tin đăng nhập!');
      }
    } catch (error) {
      console.error('Lỗi đăng nhập:', error);
      alert('Đăng nhập thất bại.');
    }
  };

  return (
    <section className="login-section">
      <div className="login-container">
        <h2>ĐĂNG NHẬP</h2>
        <p>Nếu bạn có một tài khoản, xin vui lòng đăng nhập</p>
        <form id="loginForm" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email *</label>
            <input
              type="email"
              id="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Mật khẩu *</label>
            <input
              type="password"
              id="password"
              placeholder="Mật khẩu"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="login-btn">Đăng nhập</button>
        </form>

        <p>
          Bạn chưa có tài khoản? <a href="/register">Đăng ký tại đây</a>
        </p>
        <p>
          Bạn quên mật khẩu? <a href="/reset-password">Lấy lại tại đây</a>
        </p>

        <div className="social-login">
          <button className="facebook" onClick={loginWithFacebook}>
            <img
              src="/img/anh24.jpg"
              alt="Facebook"
              style={{ width: '20px', height: '20px', marginRight: '5px' }}
            />
            Facebook
          </button>
          <button className="google" onClick={loginWithGoogle}>
            <img
              src="/img/anh23.png"
              alt="Google"
              style={{ width: '20px', height: '20px', marginRight: '5px' }}
            />
            Google
          </button>
        </div>
      </div>
    </section>
  );
}

export default Login;
