import React, { useState } from 'react';
import { updateProfile } from '../../api/auth';
import './form.css'

function Profile() {
  const [formData, setFormData] = useState({
    ho_ten: '',
    email: '',
    dia_chi: '',
    so_dien_thoai: '',
  });

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const result = await updateProfile(formData, token);
    if (result.success) {
      alert('Cập nhật thông tin thành công!');
      localStorage.setItem('userName', result.user.ho_ten); 
    } else {
      alert(result.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="ho_ten" value={formData.ho_ten} onChange={handleChange} placeholder="Họ tên" />
      <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" />
      <input name="dia_chi" value={formData.dia_chi} onChange={handleChange} placeholder="Địa chỉ" />
      <input name="so_dien_thoai" value={formData.so_dien_thoai} onChange={handleChange} placeholder="Số điện thoại" />
      <button type="submit">Cập nhật</button>
    </form>
  );
}

export default Profile;
