import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { registerUser } from '../../api/auth';
import './register.css';

function Register() {
  const [formData, setFormData] = useState({
    ho_ten: '',
    ten_dang_nhap: '',
    so_dien_thoai: '',
    email: '',
    dia_chi: '',
    mat_khau: '',
    mat_khau_xac_nhan: '',
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const formRef = useRef(null);
  const navigate = useNavigate();

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (errors[name]) setErrors(prev => ({ ...prev, [name]: '' }));
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.ho_ten.trim()) newErrors.ho_ten = 'Họ tên không được để trống';
    if (!formData.ten_dang_nhap.trim()) newErrors.ten_dang_nhap = 'Tên đăng nhập không được để trống';
    else if (!/^[a-zA-Z0-9_]+$/.test(formData.ten_dang_nhap)) newErrors.ten_dang_nhap = 'Tên đăng nhập chỉ chứa chữ, số, dấu gạch dưới';
    if (!/^(0[3|5|7|8|9])[0-9]{8}$/.test(formData.so_dien_thoai)) newErrors.so_dien_thoai = 'Số điện thoại không hợp lệ';
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) newErrors.email = 'Email không hợp lệ';
    if (formData.mat_khau.length < 6) newErrors.mat_khau = 'Mật khẩu cần ít nhất 6 ký tự';
    if (formData.mat_khau !== formData.mat_khau_xac_nhan) newErrors.mat_khau_xac_nhan = 'Mật khẩu xác nhận không khớp';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const resetForm = () => {
    setFormData({
      ho_ten: '',
      ten_dang_nhap: '',
      so_dien_thoai: '',
      email: '',
      dia_chi: '',
      mat_khau: '',
      mat_khau_xac_nhan: '',
    });
    setErrors({});
    formRef.current.reset();
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!validateForm()) return;

    setIsLoading(true);
    const payload = {
      ho_ten: formData.ho_ten.trim(),
      ten_dang_nhap: formData.ten_dang_nhap.trim(),
      so_dien_thoai: formData.so_dien_thoai.trim(),
      email: formData.email.trim().toLowerCase(),
      dia_chi: formData.dia_chi.trim(),
      mat_khau: formData.mat_khau,
    };

    try {
      const result = await registerUser(payload);
      if (result?.success || result?.status === 200) {
        alert('Đăng ký thành công!');
        resetForm();
        navigate('/login');
      } else {
        alert(result?.message || 'Đăng ký thất bại.');
      }
    } catch (error) {
      alert(error.response?.status === 409 ? 'Tên đăng nhập hoặc email đã tồn tại.' : 'Lỗi hệ thống, thử lại sau.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <section className="register-section">
      <div className="register-container">
        <h2>Đăng ký</h2>
        <form onSubmit={handleSubmit} autoComplete="off" ref={formRef}>
          {[
            { id: 'ho_ten', label: 'Họ và tên', type: 'text', required: true },
            { id: 'ten_dang_nhap', label: 'Tên đăng nhập', type: 'text', required: true },
            { id: 'so_dien_thoai', label: 'Số điện thoại', type: 'tel', required: true },
            { id: 'email', label: 'Email', type: 'email', required: true },
            { id: 'dia_chi', label: 'Địa chỉ', type: 'text' },
            { id: 'mat_khau', label: 'Mật khẩu', type: 'password', autoComplete: 'new-password', required: true },
            { id: 'mat_khau_xac_nhan', label: 'Xác nhận mật khẩu', type: 'password', autoComplete: 'new-password', required: true },
          ].map(({ id, label, type, required, autoComplete }) => (
            <div className="form-group" key={id}>
              <label htmlFor={id}>{label} {required && <span className="required">*</span>}</label>
              <input
                id={id}
                name={id}
                type={type}
                placeholder={label}
                value={formData[id]}
                onChange={handleChange}
                className={errors[id] ? 'error' : ''}
                disabled={isLoading}
                required={required}
                autoComplete={autoComplete}
              />
              {errors[id] && <span className="error-message">{errors[id]}</span>}
            </div>
          ))}
          <button type="submit" className="login-btn" disabled={isLoading}>
            {isLoading ? 'Đang đăng ký...' : 'Đăng ký'}
          </button>
        </form>
        <p>Bạn đã có tài khoản? <a href="/login">Đăng nhập</a></p>
        <div className="social-login">
          <button className="facebook" onClick={() => window.location.href = "https://www.facebook.com/login"} disabled={isLoading}>
            <img src="img/anh24.jpg" alt="Facebook" style={{ width: 20, height: 20, marginRight: 5 }} />
            Facebook
          </button>
          <button className="google" onClick={() => window.location.href = "https://accounts.google.com"} disabled={isLoading}>
            <img src="img/anh23.png" alt="Google" style={{ width: 20, height: 20, marginRight: 5 }} />
            Google
          </button>
        </div>
      </div>
    </section>
  );
}

export default Register;
