import React, { useState } from 'react';
import './login.css';

function Login() {
  const [tenDangNhap, setTenDangNhap] = useState('');
  const [matKhau, setMatKhau] = useState('');

  const loginWithFacebook = () => {
    window.location.href = 'https://www.facebook.com/login';
  };

  const loginWithGoogle = () => {
    window.location.href = 'https://accounts.google.com';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/dang-nhap', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ten_dang_nhap: tenDangNhap,
          mat_khau: matKhau,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert('Đăng nhập thành công!');
        // Lưu token nếu cần
        // localStorage.setItem('token', data.token);
      } else {
        alert(data.message || 'Sai tên đăng nhập hoặc mật khẩu!');
      }
    } catch (error) {
      alert('Lỗi kết nối đến server');
      console.error('Error:', error);
    }
  };

  return (
    <section className="login-section">
      <div className="login-container">
        <h2>ĐĂNG NHẬP</h2>
        <p>Nếu bạn có một tài khoản, xin vui lòng đăng nhập</p>
        <form id="loginForm" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="tenDangNhap">Tên đăng nhập *</label>
            <input
              type="text"
              id="tenDangNhap"
              placeholder="Tên đăng nhập"
              value={tenDangNhap}
              onChange={(e) => setTenDangNhap(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="matKhau">Mật khẩu *</label>
            <input
              type="password"
              id="matKhau"
              placeholder="Mật khẩu"
              value={matKhau}
              onChange={(e) => setMatKhau(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="login-btn">Đăng nhập</button>
        </form>
        <p>
          Bạn chưa có tài khoản? <a href="/register">Đăng ký tại đây</a>
        </p>
        <p>
          Bạn quên mật khẩu? <a href="/reset-password">Lấy lại tại đây</a>
        </p>

        <div className="social-login">
          <button className="facebook" onClick={loginWithFacebook}>
            <img
              src="/img/anh24.jpg"
              alt="Facebook"
              style={{ width: '20px', height: '20px', marginRight: '5px' }}
            />
            Facebook
          </button>
          <button className="google" onClick={loginWithGoogle}>
            <img
              src="/img/anh23.png"
              alt="Google"
              style={{ width: '20px', height: '20px', marginRight: '5px' }}
            />
            Google
          </button>
        </div>
      </div>
    </section>
  );
}

export default Login;
