    // Validate s·ªë ƒëi·ªán tho·∫°i Vi·ªát Nam
    if (!formData.so_dien_thoai.trim()) {
      newErrors.so_dien_thoai = 'S·ªë ƒëi·ªán tho·∫°i kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng';
    } else if (!/^(0[3|5|7|8|9])+([0-9]{8})$/.test(formData.so_dien_thoai)) {
      newErrors.so_dien_thoai = 'S·ªë ƒëi·ªán tho·∫°i kh√¥ng h·ª£p l·ªá (VD: 0901234567)';
    }

    // Validate email
    if (!formData.email.trim()) {
      newErrors.email = 'Email kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Email kh√¥ng h·ª£p l·ªá';
