    // Validate m·∫≠t kh·∫©u
    if (!formData.mat_khau) {
      newErrors.mat_khau = 'M·∫≠t kh·∫©u kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng';
    } else if (formData.mat_khau.length < 6) {
      newErrors.mat_khau = 'M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±';
    } else if (!/(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(formData.mat_khau)) {
      newErrors.mat_khau = 'M·∫≠t kh·∫©u ph·∫£i ch·ª©a √≠t nh·∫•t 1 ch·ªØ hoa, 1 ch·ªØ th∆∞·ªùng v√† 1 s·ªë';
    }

    // Validate x√°c nh·∫≠n m·∫≠t kh·∫©u
    if (!formData.mat_khau_xac_nhan) {
      newErrors.mat_khau_xac_nhan = 'Vui l√≤ng x√°c nh·∫≠n m·∫≠t kh·∫©u';
    } else if (formData.mat_khau !== formData.mat_khau_xac_nhan) {
      newErrors.mat_khau_xac_nhan = 'M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp';
    }

    setErrors(newErrors);
