      // Ki·ªÉm tra c√°c tr∆∞·ªùng h·ª£p th√†nh c√¥ng c√≥ th·ªÉ c√≥
      const isSuccess = 
        result?.success === true ||
        result?.status === 'success' ||
        result?.status === 200 ||
        result?.code === 200 ||
        (result?.message && result.message.toLowerCase().includes('th√†nh c√¥ng')) ||
