        if (status === 409) {
          alert('‚ùå T√™n ƒëƒÉng nh·∫≠p ho·∫∑c email ƒë√£ t·ªìn t·∫°i.');
        } else if (status === 400) {
          alert(`‚ùå ${data?.message || 'D·ªØ li·ªáu kh√¥ng h·ª£p l·ªá.'}`);
        } else {
          alert('‚ùå C√≥ l·ªói x·∫£y ra t·ª´ ph√≠a server. Vui l√≤ng th·ª≠ l·∫°i sau.');
        }
