        // L·ªói network
        alert('‚ùå Kh√¥ng th·ªÉ k·∫øt n·ªëi ƒë·∫øn server. Vui l√≤ng ki·ªÉm tra k·∫øt n·ªëi m·∫°ng.');
      } else {
        // L·ªói kh√°c
        alert('‚ùå C√≥ l·ªói kh√¥ng x√°c ƒë·ªãnh. Vui l√≤ng th·ª≠ l·∫°i.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <section className="register-section">
      <div className="register-container">
        <h2>TH√îNG TIN C√Å NH√ÇN</h2>
        <form onSubmit={handleSubmit} noValidate>
          <div className="form-group">
            <label htmlFor="ho_ten">
              H·ªç v√† t√™n <span className="required">*</span>
            </label>
            <input
              type="text"
              id="ho_ten"
              name="ho_ten"
              placeholder="Nh·∫≠p h·ªç v√† t√™n ƒë·∫ßy ƒë·ªß"
              value={formData.ho_ten}
              onChange={handleChange}
              className={errors.ho_ten ? 'error' : ''}
              disabled={isLoading}
              required
            />
            {errors.ho_ten && <span className="error-message">{errors.ho_ten}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="ten_dang_nhap">
              T√™n ƒëƒÉng nh·∫≠p <span className="required">*</span>
            </label>
            <input
              type="text"
              id="ten_dang_nhap"
              name="ten_dang_nhap"
              placeholder="Nh·∫≠p t√™n ƒëƒÉng nh·∫≠p (3+ k√Ω t·ª±)"
              value={formData.ten_dang_nhap}
              onChange={handleChange}
              className={errors.ten_dang_nhap ? 'error' : ''}
