import React, { useState } from 'react';
   import { addUser } from '../../api/';

   function AddUser() {
     const [form, setForm] = useState({
       ten_dang_nhap: '',
       mat_khau: '',
       email: '',
       ho_ten: '',
       vai_tro: 'khach',
     });
     const token = localStorage.getItem('token');

     const handleSubmit = async (e) => {
       e.preventDefault();
       const res = await addUser(form, token);
       alert(res.message);
       if (res.success) setForm({ ten_dang_nhap: '', mat_khau: '', email: '', ho_ten: '', vai_tro: 'khach' });
     };

     return (
       <form onSubmit={handleSubmit}>
         <h2>Thêm người dùng</h2>
         <input
           type="text"
           placeholder="Tên đăng nhập"
           value={form.ten_dang_nhap}
           onChange={e => setForm({ ...form, ten_dang_nhap: e.target.value })}
         />
         <input
           type="password"
           placeholder="Mật khẩu"
           value={form.mat_khau}
           onChange={e => setForm({ ...form, mat_khau: e.target.value })}
         />
         <input
           type="email"
           placeholder="Email"
           value={form.email}
           onChange={e => setForm({ ...form, email: e.target.value })}
         />
         <input
           type="text"
           placeholder="Họ tên"
           value={form.ho_ten}
           onChange={e => setForm({ ...form, ho_ten: e.target.value })}
         />
         <select
           value={form.vai_tro}
           onChange={e => setForm({ ...form, vai_tro: e.target.value })}
         >
           <option value="khach">Khách</option>
           <option value="admin">Admin</option>
         </select>
         <button type="submit">Thêm</button>
       </form>
     );
   }

   export default AddUser
